mapper.update:
update books set bstock = bstock + #{bstock}
where bkno=#{bkno}
	and bkcate = #{cate}

mapper.select:
select btitle, bcontent, bdate, bread
FROM board
where bno = #{bno}

select c.comment, c.cdate, c.cdel, m.mid, m.mname
FROM comments c join members m on c.mno = m.mno
where c.bno = #{bno}
