mapper.select:
SELECT bkno, bkname, bkwrite, bkimg, bkprice, bkcate, ( SELECT COUNT(*) FROM books
AND bkcate = #{bkcate}
AND bkname LIKE CONCAT('%', #{searchV}, '%') OR bkwrite LIKE CONCAT('%', #{searchV}, '%')
AND bkname LIKE CONCAT('%', #{searchV}, '%')
AND bkwrite LIKE CONCAT('%', #{searchV}, '%')
) AS count FROM books
AND bkcate = #{bkcate}
AND bkname LIKE CONCAT('%', #{searchV}, '%') OR bkwrite LIKE CONCAT('%', #{searchV}, '%')
AND bkname LIKE CONCAT('%', #{searchV}, '%')
AND bkwrite LIKE CONCAT('%', #{searchV}, '%')
ORDER BY bkno asc
LIMIT #{startRow}, #{pageSize}

mapper.insert:
INSERT INTO books (bkname, bkwrite, bkscontent, bkcontent,
bkprice, bstock, bkcate
, bkimg
)
VALUES (#{name}, #{write}, #{scontent}, #{content}, #{price},
#{bstock}, #{cate}
, #{upFile}
)

INSERT INTO rental ( mid, bkno, rdel
)
VALUES (#{mid}, #{bkno}, #{rdel}
)

mapper.update:
UPDATE cart 
set total = #{sumtotal}
WHERE mid=#{mid}

UPDATE coupons 
set cdel = 0
WHERE mid=#{mid}
