mapper.select:
select btitle, bcontent, bdate, bread
from board
where bno = #{bno}

mapper.update:
update board set bdel = 0 
where bno=#{bno}
and mno=(select mno from members where mid=#{mid})

mapper.select:
select mid, mname, maddr, mbirth, mphone, memail
from members
where mno = #{mno}

mapper.update:
update members set

		mid = #{id}
		mpw = #{pw}
		mbirth = #{birth}
		maddr = #{addr}
		memail = #{email}


	mno = #{mno}

mapper.select:
select mid, count(*) as count
from members
where mid = #{id}
